import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {

  state = {
    persons : [
        {name: 'JP', age: '27'},
        {name: 'ML', age: '26'},
        {name: 'Billou', age: '21'}
    ]
  };

  switchNameHandler() {
    console.log('switchNameHandler called')

    this.setState({persons: [
      {name: 'Martine', age: '59'},
      {name: 'ML', age: '26'},
      {name: 'Billou', age: '21'}
    ]});

    this.setState((state) => {
      return {quantity: state.quantity + 1};
    });

  }

  render() {
    return (
      <div className="App">

        <button onClick={this.switchNameHandler}>GROS BOUTON</button>

        <Person age={this.state.persons[0].age} name={this.state.persons[0].name}/>
        <Person age={this.state.persons[1].age} name={this.state.persons[1].name}/>
        <Person age={this.state.persons[2].age} name={this.state.persons[2].name}/>


      </div>
    );
  }
}

export default App;
