import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {

/*
this.setState({
      persons : [
        {name: 'JP', age: '27'},
        {name: 'ML', age: '26'},
        {name: 'Billou', age: '22'}
    ]
    })
*/

  constructor(props) {
    super(props);
    this.state = {date: new Date()};
  }

  switchNameHandler() {
    console.log('switchNameHandler called')

    this.setState({
      persons : [
        {name: 'JP', age: '27'},
        {name: 'ML', age: '26'},
        {name: 'Billou', age: '22'}
    ]
    })

  }

  render() {
    return (
      <div className="App">

        <h1></h1>

        <button onClick={this.switchNameHandler}>GROS BOUTON</button>
        <Person age={this.state.persons[0].age} name={this.state.persons[0].name}/>
        <Person age={this.state.persons[1].age} name={this.state.persons[1].name}/>
        <Person age={this.state.persons[2].age} name={this.state.persons[2].name}/>
      </div>
    );
  }
}

export default App;
